"use strict";
var Vector=Vector||function(a,b,c){this.a=Number(a)||0;this.b=Number(b)||0;this.c=Number(c)||0;this.length=Math.sqrt(this.a*this.a+this.b*this.b+this.c*this.c)};Vector.prototype.toString=function(){return"("+this.a.toString()+","+this.b.toString()+","+this.c.toString()+")"};Vector.prototype.toArray=function(){return[this.a,this.b,this.c]};Vector.prototype.dot=function(a){if(Vector.prototype.isPrototypeOf(a))return this.a*a.a+this.b*a.b+this.c*a.c};Vector.prototype.cross=function(a){if(Vector.prototype.isPrototypeOf(a))return new Vector(this.b*a.c-this.c*a.b,this.c*a.a-this.a*a.c,this.a*a.b-this.b*a.a)};Vector.prototype.angle=function(a){if(Vector.prototype.isPrototypeOf(a))return 180*Math.acos(this.dot(a)/(this.length*a.length))/Math.PI};
